import { describe, test, expect } from "vitest";

import { longestPalindrome } from "./longest-palindromic-substring";

describe("longest-palindromic-substring", () => {
  test("should return bb for input bb", () => {
    expect(longestPalindrome("bb")).toBe("bb");
  });

  test("should return b for input b", () => {
    expect(longestPalindrome("b")).toBe("b");
  });

  test("should return a for input babad", () => {
    expect(longestPalindrome("babad")).toBe("aba");
  });

  test("should return a for input cbbd", () => {
    expect(longestPalindrome("cbbd")).toBe("bb");
  });

  test("should return abcdbd for input dbd", () => {
    expect(longestPalindrome("abcdbd")).toBe("dbd");
  });
  test("", () => {
    expect(
      longestPalindrome(
        ""
      )
    ).toBe("fklkf");
  });
});
